it = params[:credit] == \"on\" ? true : false","  transaction.account_id = params[:account_id].empty? ? transaction.account_id : params[:account_id]","  transaction.date = params[:date]","","  if transaction.save","    session[:message] = { :heading => 'Success', :body => 'Your changes were saved.' }","    redirect to(\"/accounts/#{params[:url_safe_name]}/transactions\")","  else","    session[:message] = { :heading => 'Update Failed', :body => 'There was a problem saving your changes.  Please try again.' }","    redirect to(\"/accounts/#{params[:url_safe_name]}/transactions/#{params[:id]}/edit\")","  end","end","","get '/accounts/:url_safe_name/transactions/:id/delete' do","  #redirect to('/login') unless @logged_in_user","","  transaction = Transaction.find_by(id: params[:id])","","  if transaction.nil?","    session[:message] = { :heading => 'Update Failed', :body => 'The transaction could not be found.  Please try again.' }","    redirect to(\"/accounts/#{params[:url_safe_name]}/transactions\")","  end","","  if transaction.destroy","    session[:message] = { :heading => 'Success', :body => 'The transaction was deleted.' }","    redirect to(\"/accounts/#{params[:url_safe_name]}/transactions\")","  else","    session[:message] = { :heading => 'Update Failed', :body => 'There was a problem saving your changes.  Please try again.' }","    redirect to(\"/accounts/#{params[:url_safe_name]}/transactions\")","  end","end","","","### /auth ###","","get '/login' do","  redirect to('/accounts') if @logged_in_user","","  erb :index","end","","post '/login' do","  user = User.find_by(username: (params[:username]).downcase)","","  if user && user.authenticate(params[:password])","    token = User.generate_random_token","    user.save_persistence_token(token)","    response.set_cookie(:p_token, value: token, expires: 1.day.from_now)","","    @logged_in_user = User.find_by_token(token)","","    redirect to('/bills/upcoming')","  else","    session[:message] = { :heading => 'Lo